import React from "react";
import {
  Box,
  CssBaseline,
  Table,
  TableBody,
  TableRow,
  TableCell,
  Typography,
} from "@mui/material";

import ReferenceLink from "./ReferenceLink";
import NavBar from "./NavBar";

//had detectable amounts of pesticides
//img-urlit tilalle?
const restaurants = [{ id: 1, name: "rafla" }];

const reviews = [
  { id: 1, restaurantId: 1, content: "Jees mesta", rating: 3, upvotes: 2 },
];

const Restaurants = () => {
  return (
    <Box>
      <CssBaseline />
      <Box
        display="flex"
        flexDirection="column"
        justifyContent="center"
        alignItems="center"
        minHeight="100vh"
      >
        <NavBar />
        <Box width="50%">
          <Typography variant="h2" textAlign="center">
            Organic Restaurants
          </Typography>
          Search for Organic restaurants nearby or in a specific city. Make sure
          to leave a comment after your visit!
        </Box>
        <Table style={{ width: "80%", columnGap: 0 }}>
          <TableBody>
            {dirtyDozenData.map((foodName, index) => (
              <TableRow key={index}>
                <TableCell>
                  <Typography fontSize={25}>{index + 1}.</Typography>
                </TableCell>
                <TableCell>
                  <Typography fontSize={30}>{foodName}</Typography>
                </TableCell>
                <TableCell>
                  <Box
                    component="img"
                    sx={{
                      width: 200,
                    }}
                    src={`/images/${foodName}.jpeg`}
                  ></Box>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
        <Box width="50%">
          Read more about the Dirty Dozen and its counterpart the Clean Fifteen
          from{" "}
          <ReferenceLink
            url="https://www.ewg.org/foodnews/summary.php"
            label="here!"
            title="EWG Dirty Dozen 2023"
          />
        </Box>
      </Box>
    </Box>
  );
};

export default Restaurants;
