import React from "react";
import {
  Box,
  CssBaseline,
  Table,
  TableBody,
  TableRow,
  TableCell,
  Typography,
  TableHead,
} from "@mui/material";

import NavBar from "./NavBar";
import { useParams } from "react-router-dom";

import {
  restaurantById,
  ratingByRestaurant,
  reviewsByRestaurant,
} from "../api";

const Restaurant = () => {
  const { id } = useParams();
  const restaurant = restaurantById(id);
  return (
    <Box>
      <CssBaseline />
      <Box
        display="flex"
        flexDirection="column"
        justifyContent="center"
        alignItems="center"
        minHeight="100vh"
      >
        <NavBar />
        <Box width="50%">
          <Typography variant="h2" textAlign="center">
            {restaurant.name}
          </Typography>
        </Box>
        <Table style={{ width: "80%", columnGap: 0 }}>
          <TableHead>
            <TableRow>
              <TableCell>Review</TableCell>
              <TableCell>Rating</TableCell>
              <TableCell>Upvotes</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {reviewsByRestaurant(id).map((review) => (
              <TableRow key={review.id}>
                <TableCell>{review.content}</TableCell>
                <TableCell>{ratingByRestaurant(restaurant.id)} / 5</TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </Box>
    </Box>
  );
};

export default Restaurant;
