import React from "react";
import {
  Box,
  CssBaseline,
  Table,
  TableBody,
  TableRow,
  TableCell,
  Typography,
  TableHead,
} from "@mui/material";

import NavBar from "./NavBar";
import { Link } from "react-router-dom";

const restaurants = [
  { id: 1, name: "rafla" },
  { id: 2, name: "raflakaks" },
];

const reviews = [
  { id: 1, restaurantId: 1, content: "Jees mesta", rating: 3, upvotes: 1 },
  { id: 2, restaurantId: 1, content: "Huippupaikka!", rating: 5, upvotes: 4 },
];

const reviewsByRestaurant = (id) =>
  reviews.filter((review) => review.restaurantId === id);

const ratingByRestaurant = (id) =>
  reviewsByRestaurant(id).length === 0
    ? 0
    : reviewsByRestaurant(id).reduce((sum, review) => sum + review.rating, 0) /
      reviewsByRestaurant(id).length;

const Restaurants = () => {
  return (
    <Box>
      <CssBaseline />
      <Box
        display="flex"
        flexDirection="column"
        justifyContent="center"
        alignItems="center"
        minHeight="100vh"
      >
        <NavBar />
        <Box width="50%">
          <Typography variant="h2" textAlign="center">
            Organic Restaurants
          </Typography>
          Search for Organic restaurants nearby or in a specific city. Make sure
          to leave a comment after your visit!
        </Box>
        <Table style={{ width: "80%", columnGap: 0 }}>
          <TableHead>
            <TableRow>
              <TableCell>Name</TableCell>
              <TableCell>Rating</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {restaurants.map((restaurant) => (
              <TableRow key={restaurant.id}>
                <TableCell>
                  <Link
                    to={`/${restaurant.id}`}
                    style={{ textDecoration: "none", color: "white" }}
                  >
                    {restaurant.name}
                  </Link>
                </TableCell>
                <TableCell>{ratingByRestaurant(restaurant.id)} / 5</TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </Box>
    </Box>
  );
};

export default Restaurants;
