import React from "react";
import {
  Box,
  CssBaseline,
  Table,
  TableBody,
  TableRow,
  TableCell,
  Typography,
  TableHead,
} from "@mui/material";

import NavBar from "./NavBar";
import { useParams } from "react-router-dom";

const restaurants = [
  { id: 1, name: "rafla" },
  { id: 2, name: "raflakaks" },
];

const reviews = [
  { id: 1, restaurantId: 1, content: "Jees mesta", rating: 3, upvotes: 1 },
  { id: 2, restaurantId: 1, content: "Huippupaikka!", rating: 5, upvotes: 4 },
];

const reviewsByRestaurant = (id) =>
  reviews.filter((review) => review.restaurantId === id);

const ratingByRestaurant = (id) =>
  reviewsByRestaurant(id).length === 0
    ? 0
    : reviewsByRestaurant(id).reduce((sum, review) => sum + review.rating, 0) /
      reviewsByRestaurant(id).length;

const Restaurant = () => {
  const { id } = useParams();
  return (
    <Box>
      <CssBaseline />
      <Box
        display="flex"
        flexDirection="column"
        justifyContent="center"
        alignItems="center"
        minHeight="100vh"
      >
        <NavBar />
        <Box width="50%">
          <Typography variant="h2" textAlign="center"></Typography>
          Hii {id}
        </Box>
        <Table style={{ width: "80%", columnGap: 0 }}>
          <TableHead>
            <TableRow>
              <TableCell>Name</TableCell>
              <TableCell>Rating</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {restaurants.map((restaurant) => (
              <TableRow key={restaurant.id}>
                <TableCell>{restaurant.name}</TableCell>
                <TableCell>{ratingByRestaurant(restaurant.id)} / 5</TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </Box>
    </Box>
  );
};

export default Restaurant;
